pragma solidity ^0.5.0;


contract OceanBounty  {
    
        /**************  constants ********************/
    int constant WhitelistThreshold = 10;
    int constant BlacklistThreshold = -100;
    int constant punishmentMultiplier = 5;
    int public genreScore;
    int public noOfTracks;
    int public averageScore;
    
       /**************  enums ********************/
    
    enum state { sandbox, whitelist, blacklist}
    enum vote {vouch, reject}
    
           /**************  enums ********************/
    
    event voted (uint id, int vote);
    
        /************* structs ***************/
        //Struct User keeps record of each track and its history
    struct trackInfo  {
        uint hash ;       //this is a hash of all metadata (stored offchain in mongodb for that track)(also acts as track id)  
        int trackRating;                                //sum total of positive and negative votes for this track
        state currentState;        //state of the track (neutral/whitelist/blacklist)
        uint [] vouches; //an array of all user ids that vouched for the track
        uint [] rejects; //an array of all user ids that rejected the track
        mapping(uint => bool) userDidVote;
    }
    
    
   //Struct User keeps record of each user and their history  
    struct userInfo{
        int userId;
        int vouchCredits;
        int rejectCredits;
        int noOfVotesInDay;
    }
    
    /*************mappings**********/
    mapping (uint => trackInfo) public trackRegistry; //mapping storage of all tracks;
    mapping (uint => userInfo) public userResistry; //mapping storage of all users;
    mapping (uint => trackInfo) public playlist;   // playlist mapping track hash to track
    mapping (uint => uint) public timeOfVoteExpiry;//mapping to keep track if user ids in relation to their expiry time

    
    /****** functions *********/
    //temporry function to create new tracks
    function proposeTrack(uint _hash, uint userId)public{
        
        require(_hash != 0, 'please check track hash is not 0 and retry'); //ensure trackhash input is not 0
        require(trackRegistry[_hash].hash == 0, 'this track hash has been used already please try again'); //check if track has been created already
        
        trackInfo memory track ;
        track.hash = _hash;
        
        updateScore(_hash,track.trackRating); //user of the proposed track automatically vouches for the track
        updateUserIdToVote(_hash, track.trackRating,userId); // update arrays of users and their vote
        trackRegistry[_hash] = track;//store track in registry
        noOfTracks++; //increment the number of tracks
    }
    
    
    
    //this function vouches or rejects the given track based on user vote 
    function VouchOrReject(uint trackHash, int didVouch, uint userId)public {
        
        //update above mappings appropritely after calculating new state of the 
        //track in playlist and appropriate credits for this user and other users who voted for this track
        
        trackInfo storage track = trackRegistry[trackHash];//retrieve track information from storage
        require(!track.userDidVote[userId], 'you have already voted on this track'); //check if user has previously voted for this track
        require(checkNoOfVotesPerDay(userId),'you have exceeded your number of votes for today, please try after 24hrs');//check if user has votes more than 10 times in a day
        require((didVouch == -1 || didVouch == 1 ), 'ensure that didvouch paramater is either -1 or 1'); // require didvouch param to be either equal to -1 or 1 to avoid excessive lines of code.(-1 = reject, 1= vouch). This code line can easliy be updated in future to allow more voting options
        
        updateScore(trackHash,didVouch);
        updateState(trackHash,userId);//call function to update state 
        updateCredits(trackHash,didVouch);//update credits to users
        updateUserIdToVote(trackHash, didVouch,userId); // update arrays of users and their votes
        
        track.userDidVote[userId] = true; //update user voting status as true(voted)
        
        emit voted(userId, didVouch);
    }
    
    
    
    //internal function to update the state of each track and user based on specified mechanics
    function updateState(uint trackHash, uint userId)internal {
        trackInfo storage track = trackRegistry[trackHash];
        
        if(uint8(track.currentState) == uint8(state.sandbox) && track.trackRating > WhitelistThreshold) {
            uint8(track.currentState) == uint8(state.whitelist);
            playlist[trackHash] = track;  // if whitelisted, save track to playlist
        }else if(uint8(track.currentState) == uint8(state.sandbox) && track.trackRating < BlacklistThreshold) {
            uint8(track.currentState) == uint8(state.blacklist);
            blacklistPenalty(trackHash,userId); // call function to penalize vouches for blacklisted tracks
            delete trackRegistry[trackHash]; // if blacklisted, then remove from registry
            noOfTracks-- ; //reduce number of track count
        }
    }
    
    
      // function to update arrays of user votes which will be used to update credits  
    function updateUserIdToVote(uint trackHash, int didVouch,uint userId)internal{
        
        trackInfo storage track = trackRegistry[trackHash];
        if(didVouch == 1){
            track.vouches.push(userId); //update array of users who vouched for the track
        }
        else if(didVouch == 1){
            track.rejects.push(userId); //update array of users who rejected the track
        } 
    }
    
    
       //this function distributes credits appropriately based on game mechanics in above section
    function updateScore(uint trackHash, int didVouch)internal{
        trackInfo storage track = trackRegistry[trackHash];
        track.trackRating = track.trackRating + didVouch;//update average score
        
        } 
        
        
         //penalize voters whos tracks arre blacklisted
    function blacklistPenalty(uint trackHash, uint userId)internal{
            userInfo storage user = userResistry[userId];
            int penalityScore = trackRegistry[trackHash].trackRating * punishmentMultiplier;
            user.rejectCredits -= penalityScore; //apply penalty 
        }
    
    
    
    //this function distributes credits appropriately based on game mechanics in above section
    function checkNoOfVotesPerDay(uint userId)internal returns (bool){
        
        if (timeOfVoteExpiry[userId] == 0){ //check if its the first time the user is voting
            timeOfVoteExpiry[userId] = now + 10 days;// set expiration time
            userResistry[userId].noOfVotesInDay ++; // increase the number of votes the user has made in a day
            return true;
            
        } else if(timeOfVoteExpiry[userId] != 0){
            
            if (timeOfVoteExpiry[userId] < now){ //check if the expiry time is passed
            userResistry[userId].noOfVotesInDay = 1; // if the time is expired then reset the number of votes in a day;
            return true;
       
                } else if(timeOfVoteExpiry[userId] >now){
                    
                    if (timeOfVoteExpiry[userId] > now){ 
                        if (userResistry[userId].noOfVotesInDay < 10){ //if time is not yet expired and count is less than 10, then user can not vote
                            userResistry[userId].noOfVotesInDay++;
                            return true;
                        }else if (userResistry[userId].noOfVotesInDay > 9){//if time is not yet expired and count is greater than 9, then user can vote
                            return false;
                        }
                    }
                }
        }
    }

       


    //this function updates average score of this genre with every user action
    function updateCredits(uint trackHash, int didVouch)internal{
        
        trackInfo storage track = trackRegistry[trackHash];
        uint id ;
        
        if (didVouch == -1){
                uint length = track.rejects.length;
            
            for(uint i=0; i<length ; i++){
                id = track.rejects[i];//retrieve user id
                userResistry[id].rejectCredits++; //update relevant reject credits
            }
            } else if(didVouch == 1 ){
                uint length = track.vouches.length;
                
            for(uint i=0; i<length ; i++){
                id = track.vouches[i];//retrieve user id
                userResistry[id].vouchCredits++; //update relevant vouch credits
            }
            } 
    }
    
}
